import React, { useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';

import 'reactflow/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 300, y: 50 },
    data: { label: 'Start Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
  {
    id: 'add-node-button',
    position: { x: 300, y: 200 },
    data: { label: '+' },
    style: {
      background: '#007bff',
      color: 'white',
      padding: 10,
      borderRadius: 10,
      textAlign: 'center',
      cursor: 'pointer',
    },
  },
  {
    id: 'end',
    position: { x: 300, y: 400 },
    data: { label: 'End Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
];

const initialEdges = [
  { id: 'start->add-node-button', source: 'start', target: 'add-node-button', type: 'smoothstep' },
  { id: 'add-node-button->end', source: 'add-node-button', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(0); // To generate unique node IDs

  // Function to handle adding a new node
  const addNodeHandler = () => {
    const newNodeId = `action-node-${nodeCount}`;
    const newNodePosition = { x: 300, y: 200 + nodeCount * 100 }; // Dynamic Y position

    const newNode = {
      id: newNodeId,
      position: newNodePosition,
      data: { label: `Action Node ${nodeCount + 1}` },
      style: {
        background: '#a0d911', // Green background for Action Node
        padding: 10,
        borderRadius: 10,
        color: 'black',
        border: '1px solid #000000',
      },
    };

    const newAddNodeButton = {
      id: 'add-node-button',
      position: { x: 300, y: newNodePosition.y + 100 },
      data: { label: '+' },
      style: {
        background: '#007bff',
        color: 'white',
        padding: 10,
        borderRadius: 10,
        textAlign: 'center',
        cursor: 'pointer',
      },
    };

    // Add the new node and update the "Add Node Button"
    setNodes((nds) => {
      const updatedNodes = nds.filter((node) => node.id !== 'add-node-button'); // Remove the old "Add Node Button"
      return [...updatedNodes, newNode, newAddNodeButton];
    });

    // Update edges: Connect new node to previous and to add-node-button
    setEdges((eds) => {
      const updatedEdges = eds.filter(
        (edge) => edge.source !== 'add-node-button' && edge.target !== 'end'
      );
      return [
        ...updatedEdges,
        {
          id: `e-${nodes[nodes.length - 2]?.id || 'start'}-${newNodeId}`,
          source: nodes[nodes.length - 2]?.id || 'start', // Connect to the previous node or "Start Node"
          target: newNodeId,
          type: 'smoothstep',
        },
        {
          id: `e-${newNodeId}-add-node-button`,
          source: newNodeId,
          target: 'add-node-button',
          type: 'smoothstep',
        },
        {
          id: `e-add-node-button-end`,
          source: 'add-node-button',
          target: 'end',
          type: 'smoothstep',
        },
      ];
    });

    // Increment node count
    setNodeCount(nodeCount + 1);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Node Button */}
      <button
        onClick={addNodeHandler}
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Node
      </button>
    </div>
  );
}
