import React, { useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';

import 'reactflow/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 300, y: 50 },
    data: { label: 'Start Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
  {
    id: 'end',
    position: { x: 300, y: 400 },
    data: { label: 'End Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
];

const initialEdges = [
  { id: 'start->end', source: 'start', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(1); // To generate unique node IDs

  // Function to handle adding a new Action Node at the clicked position
  const addNodeHandler = () => {
    const newNodeId = `node-${nodeCount}`;
    const newNodePosition = { x: 300, y: 100 + nodeCount * 100 }; // Dynamic Y position

    const newNode = {
      id: newNodeId,
      position: newNodePosition,
      data: { label: `Node ${nodeCount}` },
      style: {
        background: '#a0d911', // Green background for Action Node
        padding: 10,
        borderRadius: 10,
        color: 'black',
        border: '1px solid #000000',
      },
    };

    // Add the new node
    setNodes((nds) => [...nds, newNode]);

    // Update edges: Connect the new action node and adjust the flow
    setEdges((eds) => {
      const updatedEdges = [...eds];

      // Connect the new node to the last node added, or 'start' if it's the first one
      const lastNodeId = nodes[nodes.length - 1].id || 'start';

      updatedEdges.push({
        id: `e-${lastNodeId}-${newNodeId}`,
        source: lastNodeId,
        target: newNodeId,
        type: 'smoothstep',
      });

      // Connect the last node to 'end'
      updatedEdges.push({
        id: `e-${newNodeId}-end`,
        source: newNodeId,
        target: 'end',
        type: 'smoothstep',
      });

      return updatedEdges;
    });

    // Increment node count for unique node ID
    setNodeCount(nodeCount + 1);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Node Button */}
      <button
        onClick={addNodeHandler}
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Node
      </button>
    </div>
  );
}
