import React, { useCallback, useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'react-flow-renderer';

import 'react-flow-renderer/dist/style.css';

const initialNodes = [
  { id: 'start', position: { x: 0, y: 0 }, data: { label: 'Start Node' }, style: { background: '#ffcc00', padding: 10, borderRadius: 5 } },
  { id: 'end', position: { x: 300, y: 200 }, data: { label: 'End Node' }, style: { background: '#66ccff', padding: 10, borderRadius: 5 } },
];

const initialEdges = [];

export default function App() {
  // State for nodes and edges
  const [nodes, , onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // Function to handle adding an edge between nodes
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Handle click of the "Add Edge" button
  const addEdgeHandler = () => {
    const newEdge = {
      id: 'e-start-end',
      source: 'start', // Source node ID
      target: 'end',   // Target node ID
      type: 'smoothstep', // Smooth step edge for visual aesthetics
      animated: true, // Make it animated
      style: { stroke: '#ff6347', strokeWidth: 2 }, // Custom edge style (you can adjust)
    };
    setEdges((eds) => [...eds, newEdge]);  // Add the new edge
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      Add Edge Button
      <button
        onClick={addEdgeHandler}
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Edge
      </button>
    </div>
  );
}
