import React, { useCallback, useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'react-flow-renderer';

import 'react-flow-renderer/dist/style.css';

const initialNodes = [
  { id: 'start', position: { x: 300, y: 50 }, data: { label: 'Start Node' } },
  { id: 'end', position: { x: 300, y: 200 }, data: { label: 'End Node' } },
];

const initialEdges = [
  { id: 'start->end', type: 'custom-edge', source: 'start', target: 'end' },
];

export default function App() {
  // State for nodes and edges
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // Function to handle adding an edge between nodes
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Handle click of the "Add Edge" button
  const addEdgeHandler = () => {
    const nodeType = prompt("Choose node type (Action / If/Else)").toLowerCase();

    let newNode = null;

    if (nodeType === 'action') {
      newNode = {
        id: `action-${nodes.length + 1}`,
        position: { x: 300, y: 150 },
        data: { label: 'Action Node' },
      };
    } else if (nodeType === 'if/else') {
      newNode = {
        id: `ifelse-${nodes.length + 1}`,
        position: { x: 300, y: 150 },
        data: { label: 'If/Else Node' },
      };
    } else {
      alert('Invalid node type. Please type "Action" or "If/Else".');
      return;
    }

    // Add the new node to the flow
    setNodes((nds) => [...nds, newNode]);

    // Add an edge from the "Start Node" to the new node
    const newEdge = {
      id: `start-${newNode.id}`,
      source: 'start',
      target: newNode.id,
    };
    setEdges((eds) => [...eds, newEdge]);

    // Add an edge from the new node to the "End Node"
    const endEdge = {
      id: `${newNode.id}-end`,
      source: newNode.id,
      target: 'end',
    };
    setEdges((eds) => [...eds, endEdge]);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Edge Button */}
      <button
        onClick={addEdgeHandler}
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Edge
      </button>
    </div>
  );
}
