import React, { useCallback } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'react-flow-renderer';

import 'react-flow-renderer/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 250, y: 50 },
    data: { label: 'Start Node' },
    style: { background: '#ffcc00', padding: 10, borderRadius: 5 },
  },
  {
    id: 'end',
    position: { x: 250, y: 200 },
    data: { label: 'End Node' },
    style: { background: '#66ccff', padding: 10, borderRadius: 5 },
  },
];

const initialEdges = [
  { id: '', type: 'custom-edge', source: 'a', target: 'b' },
];

export default function App() {
  const [nodes, , onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // Function to handle adding an edge between nodes
  const addEdgeHandler = () => {
    const newEdge = {
      id: 'e-start-end',
      source: 'start', // Source node ID
      target: 'end',   // Target node ID
      type: 'smoothstep', // Smooth step edge for visual aesthetics
      animated: true, // Make it animated
      style: { stroke: '#ff6347', strokeWidth: 2 }, // Custom edge style
    };
    setEdges((eds) => [...eds, newEdge]); // Add the new edge
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Button */}
      <button
        onClick={addEdgeHandler}
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          padding: '10px 20px',
          background: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: 5,
          cursor: 'pointer',
        }}
      >
        Add Edge
      </button>
    </div>
  );
}
