import React, { useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'react-flow-renderer';

import 'react-flow-renderer/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 250, y: 50 },
    data: { label: 'Start Node' },
    style: { background: '#ffcc00', padding: 10, borderRadius: 5 },
  },
  {
    id: 'end',
    position: { x: 250, y: 400 },
    data: { label: 'End Node' },
    style: { background: '#66ccff', padding: 10, borderRadius: 5 },
  },
];

const initialEdges = [
  {
    id: 'e-start-end',
    source: 'start',
    target: 'end',
    type: 'smoothstep',
    animated: true,
    style: { stroke: '#ff6347', strokeWidth: 2 },
  },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(0); // To generate unique node IDs

  // Function to handle adding a new Action Node
  const addNodeHandler = () => {
    const newNodeId = `action-${nodeCount}`;
    const newNode = {
      id: newNodeId,
      position: { x: 250, y: 200 + nodeCount * 100 }, // Position below the previous node
      data: { label: `Action Node ${nodeCount + 1}` },
      style: { background: '#a0d911', padding: 10, borderRadius: 5 },
    };

    // Add the new node
    setNodes((nds) => [...nds, newNode]);

    // Connect the new node to the previous node and the end node
    const newEdges = [
      {
        id: `e-${nodes[nodes.length - 1].id}-${newNodeId}`,
        source: nodes[nodes.length - 1].id, // Connect to the last node
        target: newNodeId,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
      {
        id: `e-${newNodeId}-end`,
        source: newNodeId,
        target: 'end', // Connect to the end node
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
    ];

    // Remove the old edge to the end node and add the new edges
    setEdges((eds) => [
      ...eds.filter((edge) => edge.target !== 'end'),
      ...newEdges,
    ]);

    setNodeCount(nodeCount + 1); // Increment node count
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Node Button */}
      <button
        onClick={addNodeHandler}
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          padding: '10px 20px',
          background: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: 5,
          cursor: 'pointer',
        }}
      >
        Add Action Node
      </button>
    </div>
  );
}
