import React, { useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 300, y: 50 },
    data: { label: 'Start Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
  {
    id: 'end',
    position: { x: 300, y: 400 },
    data: { label: 'End Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },

  {
    id: '+',
    position: { x: 300, y: 350 },
    data: { label: '+' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
    onClick: () => console.log('Plus Node clicked'),
  },
];

const initialEdges = [
  { id: 'start->end', source: 'start', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(1); // To generate unique node IDs

  const addNodeHandler = () => {
    const newNodeId = `node-${nodeCount}`;
    const newNode = {
      id: newNodeId,
      position: { x: 300, y: 100 + nodeCount * 100 },
      data: { label: `Node ${nodeCount}` },
      style: {
        background: '#a0d911',
        padding: 10,
        borderRadius: 10,
        color: 'black',
        border: '1px solid #000000',
      },
    };

    // Add the new node to the state
    setNodes((nds) => [...nds, newNode]);

    // Create new edges
    const newEdges = [
      // Connect the start to the new node
      {
        id: `e-start-${newNodeId}`,
        source: 'start',
        target: newNodeId,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
      // Connect the new node to the end node
      {
        id: `e-${newNodeId}-end`,
        source: newNodeId,
        target: 'end',
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
    ];

    // If there's already a previous node, connect the new node to it
    if (nodeCount > 0) {
      const prevNodeId = `node-${nodeCount - 1}`;
      newEdges.push({
        id: `e-${prevNodeId}-${newNodeId}`,
        source: prevNodeId,
        target: newNodeId,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      });
    }

    // Update edges state
    setEdges((eds) => [...eds, ...newEdges]);

    // Increment node count
    setNodeCount(nodeCount + 1);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Node Button */}
      <button
        onClick={addNodeHandler}
        style={{
          position: 'absolute',
          top: '300px',
          left: '820px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Node
      </button>
    </div>
  );
}
