import React, { useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';

const initialNodes = [
  {
    id: 'start',
    position: { x: 300, y: 50 },
    data: { label: 'Start Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
  {
    id: 'end',
    position: { x: 300, y: 1000 },
    data: { label: 'End Node' },
    style: {
      background: '#ffffff',
      padding: 10,
      borderRadius: 10,
      color: 'black',
      border: '1px solid #000000',
    },
  },
];

const initialEdges = [
  { id: 'start->end', source: 'start', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(1); // For unique node IDs

  // Handler to add a new node
  const addNodeHandler = (prevNodeId) => {
    const newNodeId = `node-${nodeCount}`;
    const newNodePosition = { x: 300, y: 100 + nodeCount * 100 };

    const newNode = {
      id: newNodeId,
      position: newNodePosition,
      data: { label: `Node ${nodeCount}` },
      style: {
        background: '#a0d911',
        padding: 10,
        borderRadius: 10,
        color: 'black',
        border: '1px solid #000000',
      },
    };

    // Log new node position
    console.log(`New Node Created: ${newNodeId}`);
    console.log(`Position: x = ${newNodePosition.x}, y = ${newNodePosition.y}`);

    // Add the new node
    setNodes((nds) => [...nds, newNode]);

    // Create new edges
    const newEdges = [
      // Connect the previous node to the new node
      {
        id: `e-${prevNodeId}-${newNodeId}`,
        source: prevNodeId,
        target: newNodeId,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
      // Connect the new node to the end node
      {
        id: `e-${newNodeId}-end`,
        source: newNodeId,
        target: 'end',
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#1890ff', strokeWidth: 2 },
      },
    ];

    // Update the edges state
    setEdges((eds) => [...eds, ...newEdges]);

    // Increment node count
    setNodeCount(nodeCount + 1);
  };

  // Function to handle "+" button click to add a new node
  const handlePlusClick = (prevNodeId) => {
    addNodeHandler(prevNodeId);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {/* Add Node Button */}
      <button
        onClick={() => addNodeHandler('start')} // Initial node to be connected to "start"
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add Node
      </button>

      {/* Dynamically render the "+" buttons between nodes */}
      {nodes.map((node, index) => {
        if (node.id !== 'start' && node.id !== 'end') {
          return (
            <button
              key={node.id}
              onClick={() => handlePlusClick(node.id)}
              style={{
                position: 'absolute',
                top: `${node.position.y + 20}px`, // Adjust the Y positioning
                left: `${node.position.x + 200}px`, // Adjust the X positioning
                padding: '10px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              +
            </button>
          );
        }
        return null;
      })}
    </div>
  );
}
